import React from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import { Avatar, Button, Typography, makeStyles, LinearProgress } from '@material-ui/core';
import { LockOutlined } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
    root: {
        position: 'relative',
        paddingTop: theme.spacing(4),    
    },

    avatar: {
        margin: '0 auto',
        backgroundColor: theme.palette.secondary.main,
    },

    title: {
        margin: theme.spacing(2, 0, 3, 0),
        textAlign: 'center',
    },

    submit: {
        margin: theme.spacing(3, 0, 2, 0),
    },
    proress:{
        position: 'absolute',
        top: theme.spacing(1),
        left: 0,
        right: 0,
    },
}));



function RegisterForm(props) {
    //style css
    const classes = useStyles();
    // validation form
    const schema = yup.object().shape({
        fullName: yup
        .string().required('Vui lòng nhập')
        .test('Nên có ít nhất hai từ','Vui lòng nhập đầy đủ họ tên của bạn', (value)=>{
        return value.split(' ').length >= 2;
        }),
        email: yup.string().required('Vui lòng điền email của bạn')
        .email('Vui lòng điền đúng email'),
        password: yup.string().required('Vui lòng nhập mật khẩu')
        .matches(
            /^.*(?=.{8,})((?=.*[!@#$%^&*()\-_=+{};:,<.>]){1})(?=.*\d)((?=.*[a-z]){1})((?=.*[A-Z]){1}).*$/,
            'Mật khẩu chứa ít nhất 8 ký tự, một chữ hoa số và ký tự đặc biệt'
          ),
        retypePassword: yup.string().required('Vui lòng nhập lại mật khẩu')
        .oneOf([yup.ref('password'), null], 'Mật khẩu phải trùng khớp')
    });

    const form = useForm({
        defaultValues: {
            fullName: '',
            email: '',
            password: '',
            retypePassword: '',
        },
        resolver:yupResolver(schema),
    });

    // const handleSubmit = async (values) => {
    //     const {onSubmit} = props;
    //     if(onSubmit){
    //       await onSubmit(values);
    //     }
    // }

    // const {isSubmitting} = form.formState;

    return (
        <div className={classes.root}>
            <LinearProgress className={classes.proress}/>
            <Avatar className={classes.avatar}>
                <LockOutlined></LockOutlined>
            </Avatar>
            <Typography className={classes.title} component="h3" variant="h5">
                Create an Account
            </Typography>

            <form onSubmit={form.handleSubmit(handleSubmit)}>
                <InputField name="fullName" label="Full Name" form={form} />
                <InputField name="email" label="Email" form={form} />
                <Button t name="password" label="Password" form={form} />
                <PasswordField name="retypePassword" label="Retype Password" form={form} />
                <Button type="submit" className={classes.submit} variant="contained" color="primary" size="large" fullWidth> 
                    Create an Account
                </Button>
            </form>
        </div>
        
    );
}

export default RegisterForm;
